export default {
  name: "CustomSelect",
  props: {
    items: {
      type: Array,
      required: true
    },
    modelValue: String
  },
  emits: ["update:modelValue"],
  computed: {
    formatedItems() {
      return this.items.map(item => {
        return typeof item === "object" ? item : {
          value: item,
          label: item
        };
      });
    }
  }
};
